import React, { useState } from "react";
import axios from "axios";

const WeeklyBudgetForm = () => {
  const [budget, setBudget] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!budget || isNaN(budget) || budget <= 0) {
      setMessage("Please enter a valid budget amount.");
      return;
    }

    try {
      // Replace /budget with your backend endpoint if different
      const response = await axios.post("http://localhost:8080/budget", {
        weeklyBudget: parseFloat(budget),
      });

      if (response.status === 200 || response.status === 201) {
        setMessage("Budget successfully set!");
        setBudget("");
      }
    } catch (error) {
      console.error(error);
      setMessage("Failed to set budget. Try again.");
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "auto", padding: "20px" }}>
      <h2>Set Your Weekly Budget</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Weekly Budget:
          <input
            type="number"
            value={budget}
            onChange={(e) => setBudget(e.target.value)}
            placeholder="Enter amount"
            step="0.01"
            min="0"
            style={{ marginLeft: "10px" }}
          />
        </label>
        <br /><br />
        <button type="submit">Set Budget</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default WeeklyBudgetForm;
