import React, { useState } from "react";
import axios from "axios";

const IngredientInput = () => {
  const [ingredients, setIngredients] = useState([{ name: "", quantity: "", unit: "" }]);
  const [savedIngredients, setSavedIngredients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Handle input changes
  const handleChange = (index, event) => {
    const { name, value } = event.target;
    const updated = [...ingredients];
    updated[index][name] = value;
    setIngredients(updated);
  };

  // Add new empty ingredient row
  const addIngredient = () => {
    setIngredients([...ingredients, { name: "", quantity: "", unit: "" }]);
  };

  // Remove ingredient row
  const removeIngredient = (index) => {
    setIngredients(ingredients.filter((_, i) => i !== index));
  };

  // Submit all ingredients at once
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // Send ingredients as a batch
      const response = await axios.post(
        "http://localhost:8080/api/ingredients",
        ingredients
      );


      setSavedIngredients(response.data); // Update saved ingredients list
      setIngredients([{ name: "", quantity: "", unit: "" }]); // Reset form
      setLoading(false);
      alert("Ingredients saved successfully!");
    } catch (err) {
      console.error(err);
      setError("Failed to save ingredients. Please try again.");
      setLoading(false);
    }
  };

  return (
    <div className="max-w-xl mx-auto p-4 bg-white rounded-2xl shadow-md space-y-4">
      <h2 className="text-xl font-semibold text-center">Add Ingredients</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        {ingredients.map((ingredient, index) => (
          <div key={index} className="flex space-x-2 items-center">
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={ingredient.name}
              onChange={(e) => handleChange(index, e)}
              className="border p-2 rounded w-1/2"
              required
            />
            <input
              type="number"
              name="quantity"
              placeholder="Qty"
              value={ingredient.quantity}
              onChange={(e) => handleChange(index, e)}
              className="border p-2 rounded w-1/4"
              required
            />
            <input
              type="text"
              name="unit"
              placeholder="Unit (g, ml, etc.)"
              value={ingredient.unit}
              onChange={(e) => handleChange(index, e)}
              className="border p-2 rounded w-1/4"
            />
            <button
              type="button"
              onClick={() => removeIngredient(index)}
              className="text-red-500 hover:underline"
            >
              âœ•
            </button>
          </div>
        ))}

        <div className="flex justify-between">
          <button
            type="button"
            onClick={addIngredient}
            className="text-blue-600 hover:underline"
          >
            + Add another
          </button>
          <button
            type="submit"
            disabled={loading}
            className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
          >
            {loading ? "Saving..." : "Save All"}
          </button>
        </div>

        {error && <p className="text-red-600 text-center">{error}</p>}
      </form>

      {/* Show saved ingredients */}
      {savedIngredients.length > 0 && (
        <div className="mt-6">
          <h3 className="text-lg font-semibold text-center">Saved Ingredients</h3>
          <ul className="list-disc list-inside">
            {savedIngredients.map((ing) => (
              <li key={ing.id}>
                {ing.name} - {ing.quantity} {ing.unit}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default IngredientInput;
